function toggleDropdownChevronOpen(){$(this).find(".chevron_toggleable").removeClass("fa fa-chevron-up").addClass("fa fa-chevron-down")}function toggleDropdownChevronClosed(){$(this).find(".chevron_toggleable").removeClass("fa fa-chevron-down").addClass("fa fa-chevron-up")}function initChevronToggle(){$(".dropdown").on("show.bs.dropdown",toggleDropdownChevronOpen);$(".dropdown").on("hide.bs.dropdown",toggleDropdownChevronClosed)}function numberHeadings(){var indices=[];var firstHeading=2;jQuery("h2,h3").each(function(){var hIndex;hIndex=parseInt(this.nodeName.substring(1))-firstHeading;if(indices.length<=hIndex){for(var i=indices.length;i<=hIndex;i++){indices[i]=0}}else if(indices.length>hIndex+1){indices.splice(hIndex+1,indices.length)}indices[hIndex]++;var numbering=indices.join(".")+". ";jQuery(this).prepend(numbering)})}function smoothScroll(hash){var offset=$(hash).offset();if(offset){$("html, body").animate({scrollTop:$(hash).offset().top},300,function(){window.location.hash=hash})}}function initSmoothScroll(){$('a[href^="#"]').on("click",function(e){e.preventDefault();smoothScroll(this.hash)})}